export const fetchSearchedProductRequest = (query) => {
  return (dispatch) => {
    dispatch(fetchSearchedProduct());
    shopApis
      .fetchSearchedProductData(query)
      .then((res) => {
        dispatch(fetchSearchedProductSuccess(res.data));
      })
      .catch((err) => {
        dispatch(fetchSearchedProductFail(err));
      });
  };
};
